import React from 'react';
import { connect } from 'react-redux';
import '../../App.css';


class Profile extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            profileInfo: null
        };
    }

    abortController = new AbortController();
	componentDidMount() {
        fetch('/profileroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                requestType: 1
            })
        })
        .then(res => res.json())
        .then(res => this.setState({profileInfo: res}))
        .catch(e => console.error("Critical failure: " + e.message));   
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    render() {

        if(!this.state.profileInfo) {
            return (<div></div>)
        } else {
            return (
                <div className = 'profile'>
                    <br/>
                    <p><b>IME: </b>{this.state.profileInfo[0].first_name}</p>
                    <p><b>PRIIMEK: </b>{this.state.profileInfo[0].last_name}</p>
                    <p><b>NASLOV ZA POÅ ILJANJE: </b> {this.state.profileInfo[0].street + ', ' + this.state.profileInfo[0].post_number + ' ' + this.state.profileInfo[0].town}</p>
                    <p><b>TELEFON: </b>{this.state.profileInfo[0].phone_number}</p>
                    <div style = {{textAlign: 'right'}}><button className = 'btn' style = {{padding: '2px'}}>UREDI</button></div>
                </div>
            )
        }

    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };


export default connect(mapStateToProps) (Profile);
