import React from 'react';
import '../App.css';

class PasswordReset extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            success: 'NOTHING YET'
		};
    }

    abortController = new AbortController();

    sendRequestForReset = () => {
        fetch('/passwordReset', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                email: 'katja.catia@gmail.com'
            })
        }).then(res => res.json())
            .then(res => this.setState({res: res.success}));

    }

    forgotPassword = () => {
		fetch('/forgotpassword', {
            signal: this.abortController.signal})
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    render() {

        return(
            <div className = 'app'>
                <br/><br/>
                <button onClick = {this.forgotPassword} style = {{paddingLeft: '200px'}}>FORGOT PASSWORD</button>

                <form onSubmit={this.sendRequestForReset}>
                    <input type="email" name="email" value="" placeholder="Enter your email address..." style = {{width: '100px'}}/>
                    <input type="submit" value="Reset Password" />
                    <button type = 'submit' style = {{paddingLeft: '200px'}}>RESET PASSWORD</button>
                </form>
                <p style = {{backgroundColor: 'white', paddingLeft: '200px'}}>{this.state.success}</p>
            </div>
        )
    }

}

export default PasswordReset;
