import React from 'react';
import { NavLink} from "react-router-dom";
import { IoMdSearch } from "react-icons/io";
import '../App.css';
import * as Constants from '../constants';


const womanPaths = () => {
    return Constants.women.slice(1).map((category) => <li key = {category.value}><NavLink to={'/listings/women/' + category.value}>{category.label}</NavLink></li>);   
}
const manPaths = () => {
    return Constants.men.slice(1).map((category) => <li key = {category.value}><NavLink to={'/listings/men/' + category.value}>{category.label}</NavLink></li>);   
}
const kidPaths = () => {
    return Constants.kids.slice(1).map((category) => <li key = {category.value}><NavLink to={'/listings/kids/' + category.value}>{category.label}</NavLink></li>);   
}

const searchBtnStyle = {
    height: '100%',
    minWidth: '50px',
    backgroundColor: 'rgb(255, 255, 255, 0)',
    borderRadius: '0',
    margin: '0px',
    padding: '0px',
    //border: 'none'
}

const searchStyle = {
    width: '100px',
    height: '100%',
    border: 'none',
    backgroundColor: 'rgb(255, 255, 255, 0)',
    borderRadius: '0',
    margin: '0px'
}


const NavBar = () => (
        <nav id="nav">
            <ul id="navigation">
                <li><NavLink  exact to="/" className="first" activeStyle={{background: "#525050"}}>DOMOV</NavLink></li>
                <li><NavLink to="/listings/women" activeStyle={{background: "#525050"}}>ŽENSKA OBLAČILA</NavLink>
                    <ul>
                        {womanPaths()}
                    </ul>
                </li>
                <li><NavLink to="/listings/men" activeStyle={{background: "#525050"}}>MOŠKA OBLAČILA</NavLink>
                    <ul>
                        {manPaths()}
                    </ul>
                </li>
                <li><NavLink to="/listings/kids" activeStyle={{background: "#525050"}}>OTROŠKA OBLAČILA</NavLink>
                    <ul>
                        {kidPaths()}
                    </ul>
                </li>
                <li>
                    <button style = {searchBtnStyle}>
                        <IoMdSearch size = '2em'/>
                    </button>
                </li>
                <li>
                    <input
                        style = {searchStyle}
                        type="text"
                        name="search"
                        value=""
                        placeholder="Išči ..."
                        onChange>
                    </input>
                </li>
            </ul>
        </nav>
)

export default NavBar
