import React from 'react';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import { logIn, logOut } from '../../redux/actions';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';
import NavBar from '../../components/NavBar';
import AdSpace from '../../components/AdSpace';
import ClipLoader from 'react-spinners/ClipLoader';


class PaymentStatus extends React.Component {

	constructor(props) {
		super(props);
		this.state = {

        };
    }

    abortController = new AbortController();

    componentDidMount() {
        function subStrAfterChars(str, char) {
            return str.substring(str.indexOf(char) + 1);
        }
        const eventId = subStrAfterChars(window.location.href, '=');
        console.log(eventId);
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    switchRender = () => {

        if (false) {
            return <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                        <br/>
                        <h1>NAROČILO PREKLICANO!</h1>
                        <NavLink exact to = '/shoppingcart' className = 'btnb'>NAZAJ NA KOŠARICO</NavLink>
                        <br/>
                    </div>
        } else if (true) {
            return <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                        <br/>
                        {!this.state.error && <h1>NAROČILO ZAKLJUČENO!</h1>}
                        {this.state.error && <h1>NAPAKA! EDEN ALI VEČ IZDELKOV ŽAL NI VEČ NA VOLJO!</h1>}
                        <NavLink exact to = '/' className = 'btnb'>DOMOV</NavLink>
                        <br/>
                    </div>
        } else {
            return <div><br/><br/></div>
        }

    }

    render() {
        return <div className="App">
                    <UpperLine/>
                    <Header/>
                    <NavBar/>
                    <BreadCrumbs pathname = {this.props.location.pathname}/>
                    {this.switchRender()}
                    <AdSpace/>
                    <Bottom/>
                </div>

    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };

  const mapDispatchToProps = {
    logIn: logIn,
    logOut: logOut
  };

export default connect(mapStateToProps, mapDispatchToProps) (PaymentStatus);
