import React from 'react';
import '../../App.css';

class Images extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            urls: []
        }
    }

    abortController = new AbortController();

    componentDidMount() {
        this.setState({urls: this.props.photos});
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    clickImage = (index) => {
        this.props.callbackParent(index);
    }

    render() {

        let self = this;
        let divStyle = {
            left: this.props.active * (-100) + '%',
            width: 3 * (100) + '%'
        };
        let width = {
            width: (100/this.props.items.length) + '%',
            height: '100%',
            padding: '0',
            border: '5px solid white',
            backgroundColor: 'white'
        }

        const imgStyle = {
            display: "inline-block",
            maxHeight: "100%",
            maxWidth: "100%",
            textAlign: "center",
            verticalAlign: "middle"
        }

        const helper = {
            display: "inline-block",
            height: "100%",
            verticalAlign: "middle"
        }

        return(
            <div className = "slider-wrapper">
                <ul className = "slides" style={divStyle}>
                { 
                    this.props.items.map(function(m, index){
                        if(self.props.useImages === 'true') {
                            return <button id = {index} style = {width} key = {index} onClick={() => self.clickImage(index)}><span style={helper}></span><img style={imgStyle}
                            src = {'/' + self.state.urls[index]}/></button>
                        } else {
                            return <li key={index} style = {width}>{index}</li>
                        }
                    })
                }  
                </ul>
            </div>
        )

    }

}

export default Images;
