import React from 'react';
import { connect } from 'react-redux';
import '../../App.css';


class Profile extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            profileInfo: null
        };
    }

    abortController = new AbortController();
	componentDidMount() {
        fetch('/profileroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                requestType: 1
            })
        })
        .then(res => res.json())
        .then(res => this.setState({profileInfo: res}))
        .catch(e => console.error("Critical failure: " + e.message));   
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    render() {

        if(!this.state.profileInfo) {
            return (<div></div>)
        } else {
            return (
                <div className = 'profile'>
                    <br/>
                    <br/>
                    <p><b>IME:</b>{this.state.profileInfo.first_name}</p>
                    <p><b>PRIIMEK:</b> REXHA</p>
                    <p><b>NASLOV ZA POÅ ILJANJE:</b> STARA CESTA 11, 2000 MARIBOR, SLOVENIJA
                    <button style = {{backgroundColor: 'rgb(255, 255, 255, 0)', fontWeight: 'bold', border: 'none', cursor: 'pointer', fontSize: '9px'}}>(UREDI)</button></p>
                    <p><b>TELEFON:</b> 070 005 005
                    <button style = {{backgroundColor: 'rgb(255, 255, 255, 0)', fontWeight: 'bold', border: 'none', cursor: 'pointer', fontSize: '9px'}}>(UREDI)</button></p>
                </div>
            )
        }

    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };


export default connect(mapStateToProps) (Profile);
