import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { logIn, logOut } from '../../redux/actions';
import '../../App.css';


class UserListings extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            urls: [],
            listings: []
        };

        this.displayPhotos = this.displayPhotos.bind(this);

    }

    abortController = new AbortController();
	componentDidMount() {
            fetch('/sessionroutes', {signal: this.abortController.signal})
			.then(res => res.json())
            .then(res => this.displayPhotos(res.userId))
            .catch(e => console.error("Critical failure: " + e.message));   
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    displayPhotos(uid) {
        try {
            fetch('/photos', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({userId: uid})
            }).then(res => res.json())
                .then(listings => {this.setState({listings});});
        }catch (err) {
            console.log("ERROR");
        }

    }

    render() {

        const imgStyle = {
            height: "290px",
            verticalAlign: "middle",
            marginRight: "20px",
            border: '2px solid rgb(255, 255, 255, 0.5)'
        }

        const textStyle = {
            color: 'black',
            textDecoration: 'underline',
            fontWeight: '500'
        }

        if(!this.state.listings.length) {
            return (
                <div className="userListings">
                    <div style = {{textAlign: 'center'}}>
                        <h2 style = {{color: 'white'}}>NIMATE ŠE OBJAVLJENIH OGLASOV.</h2>
                    </div>
                    <div style={{width: "100%", verticalAlign: 'top', textAlign: 'right'}}>
                        <button className = 'btn'><NavLink exact to="/upload" style={{textDecoration: "none", color:"black"}}><b>+ OBJAVI NOV OGLAS</b></NavLink></button>
                    </div>
                </div>
            );
        } else {
            return (
                <div className="userListings">
                    <div style={{width: "100%", verticalAlign: 'top', textAlign: 'right'}}>
                        <button className = 'btn'><NavLink exact to="/upload" style={{textDecoration: "none", color:"black"}}><b>+ OBJAVI NOV OGLAS</b></NavLink></button>
                    </div>
                    <div className="listingWrapper">
                        {this.state.listings.map((listing) =>
                            <li key={listing.ad_id} style={{ listStyleType: "none" }}>
                                <img src={listing.photo1_url} alt={listing.photo1_url} style={imgStyle}/><span style={{verticalAlign: "middle", display: "inline-block", height: "100%"}}></span>
                                <div style = {{width: '60%', height: '80%', float: 'right', padding: '20px'}}>
                                    <p>Številka oglasa: <NavLink to={"/item?" + listing.ad_id} style = {textStyle}>{listing.ad_id}</NavLink></p>
                                    <p><NavLink to={"/edit?" + listing.ad_id} style = {textStyle}>  UREJANJE</NavLink></p>
                                    <button style = {{backgroundColor: 'rgb(255,255,255,0)', border: 'none'}}>IZBRIŠI</button>
                                </div>

                            </li>
                        )}
                    </div>

                </div>
            );

        }

    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };


export default connect(mapStateToProps) (UserListings);
