import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import Popup from 'reactjs-popup';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import NavBar from '../../components/NavBar';
import UserSideBar from './UserSideBar';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';
import AdSpace from '../../components/AdSpace';
import '../../App.css';


class PastOrders extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            userId: '0',
            orders: []
        };
    }

    abortController = new AbortController();
	componentDidMount() {
        fetch('/sessionroutes', {signal: this.abortController.signal})
        .then(res => res.json())
        .then(res => this.setState({userId: res.userId}))
        .catch(e => console.error("Critical failure: " + e.message));

        fetch('/orderroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 4
            })
        })
        .then(res => res.json())
        .then(orders => this.setState({orders}))
        .catch(e => console.error("Critical failure: " + e.message));   
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    confirmGoodsReceived = (id) => {
        console.log("CONFIRMED ORDER: " + id);
    }

    openDispute = (id) => {
        console.log("OPEN DISPUTE FOR ORDER: " + id);
    }

    switchRender = () => {

        if(this.state.userId === '0') {
            return (
                <div style = {{backgroundColor: 'rgb(255,255,255,0.5)'}}>
                    <br/><br/><br/>
                    <h2>PROSIMO PRIJAVITE SE V SISTEM!</h2>
                    <br/><br/><br/>
                </div>
            )
        }
        else if(!this.state.orders.length) {
            return (<div><h2 style = {{color: 'white'}}>NIČESAR ŠE NISTE NAROČILI.</h2></div>)
        } else {
            return (
                <div className = 'userContainer'>
                    <br/>
                    <br/>
                    <div className = 'orderListWrapper'>                    
                    {this.state.orders.map((order) =>
                        <li key={order.order_id} style={{ listStyleType: "none" }}>
                            Številka naročila: <span style={{fontWeight: 'bold'}}> {order.order_id} </span>
                            Datum naročila:  <span style={{fontWeight: 'bold'}}>{order.date_modified.substr(0,10)} </span>
                            Cena:  <span style={{fontWeight: 'bold'}}>{order.price} € </span>
                            Poštnina:  <span style={{fontWeight: 'bold'}}>{order.postage} € </span>
                            Prodajalec: <span style={{fontWeight: 'bold'}}>{order.seller} </span>
                            Status: <span style={{fontWeight: 'bold'}}>{order.status} </span>
                            Izdelki: {order.ad_ids.split(',').map(id => <NavLink to={"/item?" + id} key = {id} className = 'btn_nopad'>{id}</NavLink>)}
                            <br/>
                            {order.status === 0 && <button className = {['btn_nopad', 'btn_marg', 'btn_blue'].join(" ")} onClick = {() => this.confirmGoodsReceived(order.order_id)}>POTRDI PREJEM</button>}
                            {order.status === 0 && <button className = {['btn_nopad', 'btn_marg', 'btn_yellow'].join(" ")} onClick = {() => this.openDispute(order.order_id)}>ODPRI PRITOŽBO</button>}
                            {order.status === 2 && <button className = {['btn_nopad', 'btn_marg', 'btn_red', 'btn_nocursor'].join(" ")}>ODPRTA PRITOŽBA</button>}
                            {order.status === 1 && <button className = {['btn_nopad', 'btn_marg', 'btn_green', 'btn_nocursor'].join(" ")}>ZAKLJUČENO</button>}
                        </li>
                    )}</div>

                </div>
            )
        }
    }

    render() {

        return (
            <div className="App">
            <UpperLine/>
            <Header/>
            <NavBar/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
            <UserSideBar/>
            {this.switchRender()}
            <AdSpace/>
            <Bottom/>
            </div>
        )


    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };


export default connect(mapStateToProps) (PastOrders);
