import React from 'react';
import '../../App.css';

class PasswordReset extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            success: ''
		};
    }

    abortController = new AbortController();

    sendRequestForReset = () => {
        fetch('/passwordReset', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                email: 'katja.catia@gmail.com'
            })
        }).then(res => res.json())
            .then(res => this.setState({res: res.success}));
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    render() {

        return(
            <div className = 'app'>
                <button onClick = {this.sendRequestForReset}>RESET PASSWORD</button>
                <p style = {{backgroundColor: 'white'}}>{this.state.success}</p>
            </div>
        )
    }

}

export default PasswordReset;
