import React from 'react';
import { connect } from 'react-redux';
import { logIn, logOut } from '../../redux/actions'
import '../../App.css';
import UpperLine from '../../components/UpperLine';
import NavBar from '../../components/NavBar';
import AdSpace from '../../components/AdSpace';
import Header from '../../components/Header';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';



class ContentSupport extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            questionType: 0,
            msg: '',
            success: false
		};
    }

    abortController = new AbortController();
    componentWillUnmount() {
        this.abortController.abort();
    }

    typeBtnclick = (event) => {
        const id = event.target.id;
        this.setState({questionType: id});
    }

    updateMsg = (event) => {
        const {value} = event.target;
        this.setState({msg: value});
    }

    onClickSend = () => {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 'content',
                questionType: this.state.questionType,
                userId: this.props.userId,
                msg: this.state.msg,
            })
        }).then(res => res.json())
        .then(res => res.success && this.setState({success: true}))
        .catch(e => console.error("Critical failure: " + e.message));
    }


    render() {

        let userId = this.props.loggedInUserId;
        let success = this.state.success;

        if(userId === '0' && !success) {
            return (
            <div className="App">
                <UpperLine/>
                <Header/>
                <NavBar/>
                <BreadCrumbs pathname = {this.props.location.pathname}/>
                <div style = {{backgroundColor: 'rgb(255,255,255,0.5)'}}>
                        <br/><br/><br/>
                        <h2>PROSIMO, DA SE PRED ODDAJO VPRAŠANJA PRIJAVITE.</h2>
                        <br/><br/><br/>
                    </div>
                <AdSpace/>
            <Bottom/>
        </div>)
        }
        else if(success) {
            return (
                <div className="App">
                    <UpperLine/>
                    <Header/>
                    <NavBar/>
                    <BreadCrumbs pathname = {this.props.location.pathname}/>
                    <div style = {{backgroundColor: 'rgb(255,255,255,0.5)'}}>
                            <br/><br/><br/>
                            <h2>HVALA ZA ODDANO VPRAŠANJE. ODGOVORILI VAM BOMO PO E-POŠTI V NAJKRAJŠEM MOŽNEM ČASU.</h2>
                            <br/><br/><br/>
                        </div>
                    <AdSpace/>
                <Bottom/>
            </div>)
        }
        else
        return (
            <div className="App">
                <UpperLine/>
                <Header/>
                <NavBar/>
                <BreadCrumbs pathname = {this.props.location.pathname}/>
                <div style = {{display: 'inline-block'}}>
                    <button id = {1} className = 'support_btn' onClick = {this.typeBtnclick}>VPRAŠANJE V ZVEZI S PRODAJO</button>
                    <button id = {2} className = 'support_btn' onClick = {this.typeBtnclick}>VPRAŠANJE V ZVEZI Z NAKUPOM</button>
                    <button id = {3} className = 'support_btn' onClick = {this.typeBtnclick}>REKLAMACIJA IZDELKA</button>
                </div>
                {this.state.questionType ?
                <div>
                    <br/>
                    <textarea
                        style={{width: '60vw', minHeight: '10px'}}
                        onChange={this.updateMsg}
                        value={this.state.msg}
                        placeholder="Novo sporočilo"
                        maxLength="990">
                    </textarea>
                    <br/>
                    <button className = 'btn' style = {{border: '2px solid #ccc'}} onClick = {this.onClickSend}>SEND</button>
                </div> : null}
                <AdSpace/>
                <Bottom/>
            </div>
        );

  }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId,
    };
  };

export default connect(mapStateToProps) (ContentSupport);
