import React, { Component } from 'react';
import { connect } from 'react-redux';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import { NavLink } from 'react-router-dom';
import BreadCrumbs from '../../components/BreadCrumbs';
import NavBar from '../../components/NavBar';
import '../../App.css';


class ChatPage extends Component {
	constructor(props) {
		super(props);
        this.state = {
            conversations: []
        }
    }

    abortController = new AbortController();
    componentDidMount() {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 3
            })
        })
        .then(res => res.json())
        .then(res => {this.setState({conversations: res})})
        .catch(e => console.error("Critical failure: " + e.message));
    }

    render() {
        return (
            <div className="App">
            <UpperLine/>
            <Header/>
            <NavBar/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
                <div style = {{backgroundColor: 'rgb(255, 255, 255, 0.5)', textAlign: 'left', paddingLeft: '100px', paddingBottom: '50px'}}>
                    <br/>
                    <h2>POGOVORI:</h2>
                    {this.state.conversations.map((conv, index) => {
                        let iLId = 0;
                        let conversant = '';
                        conv.id1 === this.props.loggedInUserId ? iLId = conv.id2 : iLId = conv.id1;
                        conv.conversant1 === this.props.loggedInUser ? conversant = conv.conversant2 : conversant = conv.conversant1;
                        return <li key = {index} style = {{listStyleType: 'none', paddingLeft: '200px', paddingTop: '10px'}}>
                                <NavLink style = {{color: 'black', textTransform: 'uppercase', fontWeight: 'bold'}} exact to = {'/chatpage/conversation?' + iLId}>{conversant}</NavLink>
                            </li>
                    })}
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
};

export default connect(mapStateToProps) (ChatPage)
