import React, { Component } from 'react';
import { connect } from 'react-redux';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import NewMsg from './NewMsg';
import '../../App.css';
import { Redirect, NavLink } from 'react-router-dom';
import NavBar from '../../components/NavBar';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';


class Sent extends Component {
	constructor(props) {
		super(props);
        this.state = {
            time: null,
            interlocutor: '',
            messages: [],
            newMsg: ''
        }
    }

    //abortController = new AbortController();

    componentDidMount() {
        //this.msgFetch();
        this.interval = setInterval(() => {this.forceUpdate();}, 60000);
        this.setState({messages: this.props.messages})
    }



    componentWillUnmount() {
        //this.abortController.abort();
        clearInterval(this.interval);
    }


    render() {

        const messages = this.state.messages;
        console.log(messages);
        const N = this.state.messages.length;
        const n = 5;

        return (

            <div style = {{backgroundColor: 'rgb(255, 255, 255, 0.8)'}}>
                <p>{this.state.time}</p>
                <p>{messages[0].msg}</p>
                <br/>
                <p style = {{fontSize: '30px'}}>POGOVOR S/Z: <b>{this.props.interlocutor}</b></p>
                {N > n && <button className = 'btn'><NavLink exact to = {this.props.location.pathname + '/all?' + this.props.interlocutorId} style = {{color: 'black', textDecoration: 'none'}}>POKAŽI STAREJŠA SPOROČILA</NavLink></button>}
                <br/>
                <ul style = {{display: 'inline-block', maxWidth: '60vw'}}>
                {N > n && (
                messages.slice(N-n, N)
                .map((msg, index) =>
                    <li key={index} style={{listStyleType: 'none', lineHeight: '35px'}}>
                        {msg.time.substring(8,10)}-{msg.time.substring(5,7)}-{msg.time.substring(0,4)} {msg.time.substring(11,16)}: <b>{msg.sender}</b>: {msg.msg}</li>))}
                {N <= n && this.state.messages.map((msg, index) =>
                    <li key={index} style={{listStyleType: 'none', lineHeight: '35px'}}>
                        {msg.time.substring(8,10)}-{msg.time.substring(6,7)}-{msg.time.substring(0,4)} {msg.time.substring(11,16)}: <b>{msg.sender}</b>: {msg.msg}</li>)}
                </ul>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
};

export default connect(mapStateToProps) (Sent)
