import React, {Component} from 'react';
import { FaEyeSlash, FaEye } from 'react-icons/fa';
import NumberFormat from 'react-number-format';
import Birthday from './Birthday';

class SignUpForm extends Component {

    constructor(props) {
		super(props);
		this.state = {
            visible: false,
            style: {color: 'gray'}
		};
    }

    visibilityChange = () => {
        this.setState({visible: !this.state.visible})
    }

    render() {

        function toTimestamp(year,month,day){
            var datum = new Date(Date.UTC(year,month-1,day, 0, 0, 0));
            return datum.getTime()/1000;
        }

        const style={
            textAlign: "center",
            margin: "30px",
            paddingLeft: "20vw",
            paddingRight: "20vw"
        }

        const iconStyle = {
            margin: 'auto -50px',
            paddingTop: '8px',
            border: 'none',
            backgroundColor: 'rgb(255, 255, 255, 0)',
            verticalAlign: 'middle'
        }

        return(
            <div style={style}>
                <form onSubmit={this.props.handleClickSignUp}>
                    <input
                        type="text"
                        name="firstName"
                        value={this.props.firstName}
                        placeholder="Ime"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="lastName"
                        value={this.props.lastName}
                        placeholder="Priimek"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="street"
                        value={this.props.street}
                        placeholder="Ulica in hišna številka"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <NumberFormat
                        value={this.props.postNumber}
                        placeholder='Poštna številka'
                        format='####'
                        allowNegative={false}
                        isNumericString={true}
                        onValueChange={this.props.changePostNumber}/>
                    <br/>
                    <input
                        type="text"
                        name="town"
                        value={this.props.town}
                        placeholder="Kraj"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="region"
                        value={this.props.region}
                        placeholder="Regija"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="country"
                        value={this.props.country}
                        placeholder="Država"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="nationality"
                        value={this.props.nationality}
                        placeholder="Državljanstvo"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="countryOfResidence"
                        value={this.props.countryOfResidence}
                        placeholder="Država bivanja"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <NumberFormat
                        value={this.props.birthday}
                        placeholder='Datum rojstva'
                        format='##. ##. ####'
                        allowEmptyFormatting={false}
                        allowNegative={false}
                        isNumericString={true}
                        onValueChange={this.props.changeBirthday}
                        onClick={()=> this.setState({style: {color: 'black'}})}
                        style = {this.state.style}/>
                    <br/>
                    <NumberFormat
                        value={this.props.phoneNumber}
                        placeholder='Številka mobilnega telefona'
                        format='+386 (0)## ### ###'
                        allowEmptyFormatting={false}
                        allowNegative={false}
                        isNumericString={true}
                        onValueChange={this.props.changePhoneNumber}
                        onClick={()=> this.setState({style: {color: 'black'}})}
                        style = {this.state.style}/>
                    <br/>
                    <input
                        type="email"
                        name="email"
                        value={this.props.email}
                        placeholder="E-naslov"
                        onChange={this.props.handleChange}>
                    </input>

                    <Birthday/>

                    <br/>
                    <div style = {{display: 'inline-flex', width: '100%'}}>
                    <input
                        type={this.state.visible ? 'text' : 'password'}
                        name="password"
                        value={this.props.password}
                        placeholder="Geslo"
                        onChange={this.props.handleChange}>
                    </input>
                    {this.state.visible ? <button type = 'button' style = {iconStyle} onClick = {this.visibilityChange}>
                                                <FaEyeSlash size = '2em'/>
                                            </button> :
                                            <button type = 'button' style = {iconStyle} onClick = {this.visibilityChange}>
                                                <FaEye size = '2em'/>
                                            </button>}
                    </div>

                    <br/>
                    <br/>
                    <p><b>ČE SE ŽELITE REGISTRIRATI TUDI KOT PRODAJALEC, IZPOLNITE ŠE NASLEDNJE PODATKE:</b></p>
                    <input
                        type="text"
                        name="iban"
                        value={this.props.iban}
                        placeholder="IBAN"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="bic"
                        value={this.props.bic}
                        placeholder="BIC banke"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>

                    <br/>
                    <button type = 'submit' className = 'btnb' id = 'signup'>ODDAJ</button>
                </form>
                <p>{this.props.birthday}</p>
            </div>
        );
    }

}

export default SignUpForm;
