import React from 'react';
import { connect } from 'react-redux';
import NumberFormat from 'react-number-format';
import '../../App.css';


class Profile extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            profileInfo: null,
            password: '',
            warning: '',
            edit: false
        };
    }

    abortController = new AbortController();
	componentDidMount() {
        fetch('/profileroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                requestType: 1
            })
        })
        .then(res => res.json())
        .then(res => this.setState({profileInfo: res[0]}, () => console.log(this.state.profileInfo)))
        .catch(e => console.error("Critical failure: " + e.message));   
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    onEditButtonClick = () => {
        this.setState({edit: true})
    }
    changePhoneNumber = (values) => {
        const {value} = values;
        var profileInfo = {...this.state.profileInfo}
        profileInfo.phone_number = value;
        this.setState({profileInfo});
    }
    changeAddress = (e) => { 
        var profileInfo = {...this.state.profileInfo}
        const { name, value } = e.target;
        profileInfo[name] = value;
        this.setState({profileInfo});
    }

    updateInfo = () =>{
        fetch('/profileroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                requestType: 2,
                profileInfo: this.state.profileInfo
            })
        })
        .then(res => res.json())
        .catch(e => console.error("Critical failure: " + e.message));
    }


    checkPassStrength(pass) {
        const mediumRegex = new RegExp("^(((?=.*[a-z])(?=.*[A-Z]))|((?=.*[a-z])(?=.*[0-9]))|((?=.*[A-Z])(?=.*[0-9])))(?=.{8,})");
        return mediumRegex.test(pass);
    }

    changePassword = (e) => { 
        if(!this.checkPassStrength(e.target.value)){
            this.setState({
                warning: 'Geslo mora biti dolgo vsaj 8 znakov in mora vsebovati vsaj dva od naslednjih znakov: mala črka, velika črka, številka.'
            });
        } else {
            this.setState({warning: ''});
        }
        this.setState({password: e.target.value});
    }

    updatePassword = () => {
        if(this.state.password !== '' && this.state.warning === '') {
            fetch('/profileroutes', {
                signal: this.abortController.signal,
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({
                    requestType: 3,
                    password: this.state.password
                })
            })
            .then(res => res.json())
            .then(res => {if(res.success) this.setState({success: 'Uspešno ste spremenili geslo.'})})
            .catch(e => console.error("Critical failure: " + e.message));
        }
    }

    render() {

        if(!this.state.profileInfo) {
            return (<div></div>)
        } else {
            return (
                <div className = 'profile'>
                    <br/>
                    <p><b>IME: </b>{this.state.profileInfo.first_name}</p>
                    <p><b>PRIIMEK: </b>{this.state.profileInfo.last_name}</p>
                    <p><b>NASLOV ZA POŠILJANJE: </b> {this.state.profileInfo.street + ', ' + this.state.profileInfo.post_number + ' ' + this.state.profileInfo.town}</p>
                    <p><b>NASLOV ZA POŠILJANJE: </b><form onSubmit = {this.updateInfo}>
                                                    <input type='text' id='street' name='street' value={this.state.profileInfo.street} style = {{width: '200px'}} onChange={this.changeAddress}/>
                                                    <input type='number' id='postnumber' name='post_number' value={this.state.profileInfo.post_number} style = {{width: '100px'}} onChange={this.changeAddress}/>
                                                    <input type='text' id='town' name='town' value={this.state.profileInfo.town} style = {{width: '150px'}} onChange={this.changeAddress}/>
                                                    <br/>
                                                    <b>TELEFON: </b> +386 (0)<NumberFormat
                                                                                    value={this.state.profileInfo.phone_number}
                                                                                    placeholder='__ ___ ___'
                                                                                    format='## ### ###'
                                                                                    //allowEmptyFormatting mask="_"
                                                                                    allowNegative={false}
                                                                                    isNumericString={true}
                                                                                    onValueChange={this.changePhoneNumber}
                                                                                    style = {{width: '150px'}}/>
                                                    <br/>
                                                    <button className = 'btnb' type = 'submit'>SHRANI</button>
                                                    </form></p>
                    <p><b>TELEFON: </b>{'0' + this.state.profileInfo.phone_number}</p>
                    SPREMENI GESLO: <input type='password' id='pass' name='pass' value={this.state.password} style = {{width: '200px'}} onChange={this.changePassword}/>
                    <p style = {{color: 'red', lineHeight: '40px'}}>{this.state.warning}</p>
                    <div style = {{textAlign: 'right'}}><button className = 'btnu'>UREDI</button></div>
                </div>
            )
        }

    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };


export default connect(mapStateToProps) (Profile);
