import React, { Component } from 'react';
import { connect } from 'react-redux';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import NewMsg from './NewMsg';
import Sent from './Sent';
import '../../App.css';
import { Redirect, NavLink } from 'react-router-dom';
import NavBar from '../../components/NavBar';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';


class Conversation extends Component {
	constructor(props) {
        super(props);
        this.state = {
            time: null,
            interlocutor: '',
            messages: [],
            newMsg: ''
        }
    }

    abortController = new AbortController();

    componentDidMount() {
        const iLId = this.interlocutorId();
        this.interlocutor(iLId);
        this.msgFetch();
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    interlocutorId() {
        function subStrAfterChars(str, char) {
            return str.substring(str.indexOf(char) + 1);
        }
        const interlocutorId = subStrAfterChars(window.location.href, '?');
        return interlocutorId;
    }

    interlocutor = (iLId) => {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 4,
                iLId: iLId,
            })
        }).then(res => res.json())
        .then(res => this.setState({interlocutor: res[0].first_name}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    msgFetch = () => {
        const iLId = this.interlocutorId();
        this.interlocutor(iLId);
        console.log(iLId);

        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 1,
                conversantId: iLId
            })
        })
        .then(res => res.json())
        .then(results => {
            const messages = results.map(result => ({time: result.date_created, sender: result.sender, msg: result.msg}));
            console.log(messages);
            this.setState({messages})
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }

    sendMsg = (newMsg) => {
        this.setState({newMsg})
        const receiverId = this.interlocutorId();
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 2,
                receiverId: receiverId,
                msg: newMsg
            })
        }).then(res => res.json())
        .then(res => res.success && this.setState(state => {const messages = [...state.messages, ({time: 'now', sender: this.props.loggedInUser, msg: state.newMsg})]; return {...state, messages, newMsg: ''}}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    render() {

        const interlocutorId = this.interlocutorId();
        const interlocutor = this.state.interlocutor;

        return (
            <div className = 'App'>
                <UpperLine/>
                <Header/>
                <NavBar/>
                <BreadCrumbs pathname = {this.props.location.pathname}/>
                <Sent interlocutorId = {interlocutorId} interlocutor = {interlocutor} messages = {this.state.messages}/>
                <NewMsg callBackParent = {this.sendMsg}/>
                <Bottom/>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
};

export default connect(mapStateToProps) (Conversation)
