import React from 'react';
import '../App.css';

class ForgotPassword extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            email: '',
            success: 'NOTHING YET'
        };
        this.handleChange = this.handleChange.bind(this);
    }

    abortController = new AbortController();

    sendRequestForReset = (e) => {
        e.preventDefault();

        fetch('/forgotpassword', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                email: this.state.email
            })
        }).then(res => res.json())
            .then(res => {console.log(res); this.setState({success: res.success})});

    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    handleChange(event) {
		this.setState({
			[event.target.name]: event.target.value
		})
    }

    //<button type = 'submit' style = {{paddingLeft: '200px'}}>RESET PASSWORD</button>

    render() {

        return(
            <div className = 'app'>
                <br/><br/>
                <form onSubmit={this.sendRequestForReset}>
                    <input  type = 'email'
                            name = 'email'
                            value = {this.state.email}
                            placeholder = 'Vpiši svoj e-naslov ...'
                            style = {{width: '300px'}}
                            onChange = {this.handleChange}/>
                    <button type = 'submit'>POŠLJI ZAHTEVO ZA SPREMEMBO GESLA</button>
                </form>
                <p style = {{backgroundColor: 'white', paddingLeft: '200px'}}>{this.state.success}</p>
            </div>
        )
    }

}

export default ForgotPassword;
