import React from 'react';
import { NavLink } from 'react-router-dom';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';
import CategoryListings from './CategoryListings';
import NavBar from '../../components/NavBar';
import {findIndexByValue} from '../../functions/findindex';
import * as Constants from '../../constants';


class SearchResults extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            searchFor: '',
            ads: []
        };
    }

    abortController = new AbortController();
	componentDidMount() {
        function subStrAfterChars(str, char) {
            return str.substring(str.indexOf(char) + 1);
        }
        const searchFor = subStrAfterChars(window.location.href, '?');
        console.log(searchFor);
        this.setState({searchFor}, () => this.search());
        this.search(searchFor);

    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    search = (searchFor) => {
        fetch('/search', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                signal: this.abortController.signal,
                searchFor: searchFor
            })
        }).then(res => res.json())
        .then(ads => this.setState({ads}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    onSearchChange = () => {
        function subStrAfterChars(str, char) {
            return str.substring(str.indexOf(char) + 1);
        }
        const searchFor = subStrAfterChars(window.location.href, '?');
        this.setState({searchFor});
        console.log(searchFor);
    }

    render() {

        // return <div className="App">
        //                 <UpperLine/>
        //              <Header/>
        //             <NavBar/>
        //                  <Bottom/>
        //            </div>

        // let path = '';
        // this.props.subcategory === '0' ? path = '/listings/' + this.props.category  + '/all' : path = '/listings/' + this.props.category + '/' + this.props.subcategory

        // if (this.state.ads.length === 0) {
        //     return <div className="App">
        //                 <UpperLine/>
        //                 <Header/>
        //                 <NavBar/>
        //                 <BreadCrumbs pathname = {path}/>
        //                 <div style={{display: "flex", maxWidth: "90vw", minHeight: '30vh', justifyContent: 'center'}}>
        //                     <div><h2 style = {{color: 'white'}}>OPROSTITE, NIČESAR NISMO NAŠLI!</h2></div>
        //                 </div>
        //                 <Bottom/>
        //             </div>
        // } else
        // return (
        //     <div className="App">
        //         <UpperLine/>
        //         <Header/>
        //         <NavBar/>
        //         <BreadCrumbs pathname = {path}/>
        //         <div style={{display: "flex", maxWidth: "100vw", paddingLeft: '5vw', paddingRight: '5vw'}}>
        //             <CategoryListings ads = {this.state.ads}/>
        //         </div>
        //         <Bottom/>
        //     </div>
        // );

        const imgStyle = {
            width: "100%",
            border: '2px solid white'
        }

        const wrapper = {
            display: "grid",
            gridTemplateColumns: "repeat(4, 1fr)",
            gridGap: "100px 100px",
            //border: "1px solid black",
            maxWidth: "100%",
            margin: "50px 50px"
        }

        const path = '';
        let searchFor = this.state.searchFor;


        if (this.state.ads.length === 0) {
            return <div className="App">
                        <UpperLine/>
                        <Header/>
                        <NavBar callBackParent = {this.search}/>
                        <BreadCrumbs pathname = {path}/>
                        <div style={{display: "flex", maxWidth: "90vw", minHeight: '30vh', justifyContent: 'center'}}>
                            <div><h2 style = {{color: 'white'}}>OPROSTITE, NIČESAR NISMO NAŠLI!</h2></div>
                        </div>
                        <Bottom/>
                    </div>
        } else {
            return(
                <div className = 'App'>
                <UpperLine/>
                <Header/>
                <NavBar callBackParent = {this.search}/>
                <BreadCrumbs pathname = {path}/>
            <p className = 'white-back'>Search results for: {searchFor}</p>
            <div style={{maxWidth: '100%', justifyContent: 'center', minHeight:'80vh'}}>
                <div style={wrapper}>
                    {this.state.ads.map((ad) => {
                        let category = Constants.categoryNames[ad.category]
                        let subcategory;
                        switch(category) {
                            case 'women':
                                subcategory = Constants.women[ad.subcategory].value;
                                break;
                            case 'men':
                                subcategory = Constants.men[ad.subcategory].value;
                                break;
                            case 'kids':
                                subcategory = Constants.kids[ad.subcategory].value;
                                break;
                            default:
                                subcategory = 0;
                        }
                        return (
                            <li key={ad.ad_id} style={{ listStyleType: "none" }}><NavLink exact to =
                            {'/listings/' + category + '/' + subcategory + '/item?' + ad.ad_id}>
                                <img src={'/' + ad.photo1_url} style={imgStyle}/>
                            </NavLink></li>)

                        }
                    )}
                </div>
            </div>
                                    <Bottom/></div>)
        }
    }

}

export default SearchResults;
