import React from 'react';
import { NavLink } from 'react-router-dom';
import '../../App.css';
import queryString from 'query-string';
import UploadPage from './UploadPage';
import EditPage from './EditPage';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import ImgUpload from './ImgUpload';
import SelectForms from './SelectForms';
import Bottom from '../../components/Bottom';
import ClipLoader from 'react-spinners/ClipLoader';



class UploadEditPage extends React.Component {

    render() {

        const adId = queryString.stringify(queryString.parse(this.props.location.search));

        if(adId !== '') {
            return  <EditPage adId = {adId} render = {({state, onSelectionSaved, onImageSaved, handleUpload, srcsToPass}) => 
               {if(state.editStatus) {
                   return <div className="App">
                            <h1 style={{backgroundColor: 'rgb(255, 255, 255, 0.5)'}}>USPEŠNO STE POPRAVILI OGLAS!</h1>
                            <NavLink exact to = '/user_page'><button className = 'btn'>NAZAJ NA MOJO STRAN</button></NavLink>
                        </div>
               } else if(srcsToPass !== 0) {
                return (
                <div className="App">
                    <UpperLine/>
                    <Header/>
                    <div style={{display: "flex", width: "90vw"}}>
                        <div className="sidenav"></div>
                        <div style={{width: "100%", backgroundColor: 'rgb(255, 255, 255, 0.5)'}}>
                            <h1>POPRAVI OGLAS</h1>
                            <br/>
                            <SelectForms callBackParent={onSelectionSaved} saveAtt ={state.saveAttempt} adId={adId}/>
                            <br/>
                            <p>OBDRŽI TRENUTNE FOTOGRAFIJE:</p>
                            <br/>
                            <img src={srcsToPass[0]} alt="image_one" height="200px" hspace="20"/>
                            <img src={srcsToPass[1]} alt="image_two" height="200px" hspace="20"/>
                            <img src={srcsToPass[2]} alt="image_three" height="200px" hspace="20"/>
                            <br/>
                            <br/>
                            <p>ALI IZBERI NOVE FOTOGRAFIJE:</p>
                            <ImgUpload callBackParent={onImageSaved} index='0'/>
                            <ImgUpload callBackParent={onImageSaved} index='1'/>
                            <ImgUpload callBackParent={onImageSaved} index='2'/>
                            <button className = 'btnb' onClick={handleUpload}>SHRANI OGLAS</button>

                            <p style={{color: "red"}}>{state.warning}</p>
                        </div>
                    </div>
                    <Bottom/>
                </div>
                )} else return <div className="App"><h1 style={{backgroundColor: 'rgb(255, 255, 255, 0.5)'}}>Something went wrong</h1></div>}}
                />
        } else {
            return <UploadPage adId = {adId} render = {({state, onSelectionSaved, onImageSaved, handleUpload}) => {
                if(state.uploadStatus) {
                        return <div className="App">
                                    <h1 style={{backgroundColor: 'rgb(255, 255, 255, 0.5)'}}>HVALA ZA ODDANI OGLAS!</h1>
                                    <NavLink exact to = '/user_page'><button className = 'btn'>NAZAJ NA MOJO STRAN</button></NavLink>
                                </div>
                    } else {
                        return (
                        <div className="App">
                        <UpperLine/>
                        <Header/>
                        <div style={{display: "flex", width: "90vw"}}>
                            <div className="sidenav"></div>
                            <div style={{width: "100%", backgroundColor: 'rgb(255, 255, 255, 0.5)'}}>
                                <h1>OBJAVI OGLAS</h1>
                                <br/>
                                <SelectForms callBackParent={onSelectionSaved} saveAtt ={state.saveAttempt} adId={adId}/>
                                <br/>
                                <p>Naloži tri fotografije (prva fotografija je naslovna)</p>
                                <ImgUpload callBackParent={onImageSaved} index='0'/>
                                <ImgUpload callBackParent={onImageSaved} index='1'/>
                                <ImgUpload callBackParent={onImageSaved} index='2'/>
                                <button className = 'btnb' onClick={handleUpload}>SHRANI OGLAS</button>
                                <p style={{color: "red"}}>{state.warning}</p>
                            </div>
                        </div>
                        <Bottom/>
                        </div>
                        )}
                }}
                />
            }

    }

}

export default UploadEditPage
