import React, { Component } from 'react';
import { connect } from 'react-redux';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import { NavLink } from 'react-router-dom';
import BreadCrumbs from '../../components/BreadCrumbs';
import NavBar from '../../components/NavBar';
import Bottom from '../../components/Bottom';
import AdSpace from '../../components/AdSpace';
import '../../App.css';


class ChatPage extends Component {
	constructor(props) {
		super(props);
        this.state = {
            userId: '0',
            conversations: [],
            unread: []
        }
    }

    abortController = new AbortController();
    componentDidMount() {

        fetch('/sessionroutes', {signal: this.abortController.signal})
        .then(res => res.json())
        .then(res => this.setState({userId: res.userId}, () => console.log(this.state.userId)))
        .catch(e => console.error("Critical failure: " + e.message));

        this.conversationsFetch();
        this.interval = setInterval(() => {this.conversationsFetch();}, 30000);
    }

    componentWillUnmount() {
        this.abortController.abort();
        clearInterval(this.interval);
    }


    // .then(res => {this.setState({conversations: res}, () => this.setState(state => {let unread = [];
    //     for(let i = 0; i < res.length; i++) unread.concat(0); return {...state, unread}}));
    //     if(res) {
    //         for(let i = 0; i < res.length; i++) {
    //         let iLId = 0;
    //         res[i].id1 === this.props.loggedInUserId ? iLId = res[i].id2 : iLId = res[i].id1;
    //         this.fetchNumberOfUnreadNew(i, iLId);
    //         }
    //     }
    // })

    conversationsFetch = () => {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 3
            })
        })
        .then(res => res.json())
        .then(res => {console.log(res.length); const conversations = res; let unread = []; for(let i = 0; i < res.length; i++) unread.concat(0);
            this.setState(state => {return {...state, conversations, unread}}, () => {for(let i = 0; i < res.length; i++) {
                let iLId = 0;
                res[i].id1 === this.props.loggedInUserId ? iLId = res[i].id2 : iLId = res[i].id1;
                //this.fetchNumberOfUnreadNew(i, iLId);
            }})
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }


    fetchNumberOfUnread = (i, iLId) => {
        console.log("Fetching");
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 6,
                iLId: iLId
            })
        })
        .then(res => res.json())
        .then(res => {console.log(res); this.setState(state => {
                                        const unread = state.unread.concat(res);
                                        return {...state, unread }; });                          
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }

    fetchNumberOfUnreadNew = (i, iLId) => {
        console.log("Fetching");
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 6,
                iLId: iLId
            })
        })
        .then(res => res.json())
        .then(res => {console.log(res); this.setState(state => {
                                            const unread = state.unread.map((num, j) => {if (j === i) {return res;} else return num;});
                                            return ({...state, unread});
                                        });                    
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }

    switchRender = () => {
        if (this.state.userId === '0') {
            return <div style = {{backgroundColor: 'rgb(255,255,255,0.5)'}}>
                <br/><br/><br/>
                <h2>PROSIMO PRIJAVITE SE V SISTEM!</h2>
                <br/><br/><br/>
            </div>
        }
        else return <div style = {{backgroundColor: 'rgb(255, 255, 255, 0.5)', textAlign: 'left', paddingLeft: '100px', paddingBottom: '50px'}}>
            <br/>
            <h2>POGOVORI:</h2>
            {console.log(this.state.unread)}
            {this.state.conversations.map((conv, index) => {
                let iLId = 0;
                let conversant = '';
                conv.id1 === parseInt(this.props.loggedInUserId) ? iLId = conv.id2 : iLId = conv.id1;
                conv.conversant1 === this.props.loggedInUser ? conversant = conv.conversant2 : conversant = conv.conversant1;
                let numunread = '';
                if(this.state.unread[index]) numunread = '(' + this.state.unread[index] + ')';
                return <li key = {index} style = {{listStyleType: 'none', paddingLeft: '200px', paddingTop: '10px'}}>
                        <NavLink style = {{color: 'black', textTransform: 'uppercase', fontWeight: 'bold'}} exact to = {'/chatpage/conversation?' + iLId}>{conversant + numunread}</NavLink>
                    </li>
            })}
        </div>
    }

    render() {
        return (
            <div className="App">
            <UpperLine/>
            <Header/>
            <NavBar/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
            {this.switchRender()}
            <AdSpace/>
            <Bottom/>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
};

export default connect(mapStateToProps) (ChatPage)
