import React from 'react';
import '../App.css';

class ForgotPassword extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            email: '',
            success: 'NOTHING YET'
		};
    }

    abortController = new AbortController();

    sendRequestForReset = () => {
        fetch('/forgotpassword', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                email: 'katja.catia@gmail.com'
            })
        }).then(res => res.json())
            .then(res => this.setState({res: res.success}));

    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    //<button type = 'submit' style = {{paddingLeft: '200px'}}>RESET PASSWORD</button>

    render() {

        return(
            <div className = 'app'>
                <br/><br/>
                <form onSubmit={this.sendRequestForReset}>
                    <input  type = 'email'
                            name = 'email'
                            value = {this.state.email}
                            placeholder = 'Vpiši svoj e-naslov ...'
                            style = {{width: '300px'}} />
                    <button type = 'submit'>POŠLJI ZAHTEVO ZA SPREME</button>
                </form>
                <p style = {{backgroundColor: 'white', paddingLeft: '200px'}}>{this.state.success}</p>
            </div>
        )
    }

}

export default ForgotPassword;
