import React, { Component } from 'react';
import { connect } from 'react-redux';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import { NavLink } from 'react-router-dom';
import BreadCrumbs from '../../components/BreadCrumbs';
import NavBar from '../../components/NavBar';
import '../../App.css';


class ChatPage extends Component {
	constructor(props) {
		super(props);
        this.state = {
            conversations: [],
            unread: []
        }
    }

    abortController = new AbortController();
    componentDidMount() {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 3
            })
        })
        .then(res => res.json())
        .then(res => {this.setState({conversations: res});
         if(res) {
            for(let i = 0; i < res.length; i++) {
                let iLId = 0;
                res[i].id1 === this.props.loggedInUserId ? iLId = res[i].id2 : iLId = res[i].id1;
                this.fetchNumberOfUnread(iLId);
            }
         }
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }


    fetchNumberOfUnread = (iLId) => {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 6,
                iLId: iLId
            })
        })
        .then(res => res.json())
        .then(res => {this.setState(state => {
                                        const unread = state.unread.concat(res);
                                        return {...state, unread }; });                          
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }

    render() {
        return (
            <div className="App">
            <UpperLine/>
            <Header/>
            <NavBar/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
                <div style = {{backgroundColor: 'rgb(255, 255, 255, 0.5)', textAlign: 'left', paddingLeft: '100px', paddingBottom: '50px'}}>
                    <br/>
                    <h2>POGOVORI:</h2>
                    {this.state.conversations.map((conv, index) => {
                        let iLId = 0;
                        let conversant = '';
                        conv.id1 === this.props.loggedInUserId ? iLId = conv.id2 : iLId = conv.id1;
                        conv.conversant1 === this.props.loggedInUser ? conversant = conv.conversant2 : conversant = conv.conversant1;
                        let numunread = '';
                        if(this.state.unread[index]) numunread = '(' + this.state.unread[index] + ')';
                        return <li key = {index} style = {{listStyleType: 'none', paddingLeft: '200px', paddingTop: '10px'}}>
                                <NavLink style = {{color: 'black', textTransform: 'uppercase', fontWeight: 'bold'}} exact to = {'/chatpage/conversation?' + iLId}>{conversant + numunread}</NavLink>
                            </li>
                    })}
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
};

export default connect(mapStateToProps) (ChatPage)
