import React from 'react';
import { connect } from 'react-redux';
import { logIn, logOut } from '../../redux/actions'
import '../../App.css';
import UpperLine from '../../components/UpperLine';
import NavBar from '../../components/NavBar';
import AdSpace from '../../components/AdSpace';
import Header from '../../components/Header';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';



class ContentSupport extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            questionType: 0,
            msg: ''
		};
    }

    abortController = new AbortController();
    componentWillUnmount() {
        this.abortController.abort();
    }

    typeBtnclick = (event) => {
        const id = event.target.id;
        console.log(id);
        this.setState({questionType: id});
    }

    updateMsg = (event) => {
        const {value} = event.target;
        this.setState({msg: value});
    }

    onClickSend = () => {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 2
            })
        }).then(res => res.json())
        .then(res => res.success && this.setState(state => {const messages = [...state.messages, ({time: 'now', sender: this.props.loggedInUser, msg: state.newMsg})]; return {...state, messages, newMsg: ''}}))
        .catch(e => console.error("Critical failure: " + e.message));
    }


    render() {

        let userId = this.props.loggedInUserId;
        if(!userId) {
            return <div>Prosimo, prijavite se v sistem.</div>
        }
        else
        return (
            <div className="App">
            <UpperLine/>
            <Header/>
            <NavBar/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
            <div style = {{display: 'inline-block'}}>
                <button id = {1} className = 'support_btn' onClick = {this.typeBtnclick}>VPRAŠANJE V ZVEZI S PRODAJO</button>
                <button id = {2} className = 'support_btn' onClick = {this.typeBtnclick}>VPRAŠANJE V ZVEZI Z NAKUPOM</button>
                <button id = {3} className = 'support_btn' onClick = {this.typeBtnclick}>REKLAMACIJA IZDELKA</button>
            </div>
            {this.state.questionType ?
            <div>
                <br/>
                <textarea
                    style={{width: '60vw', minHeight: '10px'}}
                    onChange={this.updateMsg}
                    value={this.state.msg}
                    placeholder="Novo sporočilo"
                    maxLength="990">
                </textarea>
                <br/>
                <button className = 'btn' style = {{border: '2px solid #ccc'}} onClick = {this.onClickSend}>SEND</button>
            </div> : null}
            <AdSpace/>
            <Bottom/>
            </div>
        );

  }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId,
    };
  };

const mapDispatchToProps = {
    logIn: logIn,
    logOut: logOut
};

export default connect(mapStateToProps, mapDispatchToProps) (ContentSupport);
