import React from 'react';
import { connect } from 'react-redux';
import { logIn, logOut } from '../../redux/actions';
import OrderListing from './OrderListing';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import Bottom from '../../components/Bottom';
import BreadCrumbs from '../../components/BreadCrumbs';
import NavBar from '../../components/NavBar';
import { NavLink } from 'react-router-dom';


class OrderPage extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            orderStatus: '',
            adIds: [],
            order: [],
            fetching: true,
            canceled: false,
            finished: false,
            error: false
        };
    }

    abortController = new AbortController();

    componentDidMount() {
        fetch('/orderroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 2,
            })
        }).then(res => res.json())
            .then(res => { const adIds = JSON.parse(res); this.setState({adIds});
                (adIds.length) && fetch('/cartListings', {
                                    signal: this.abortController.signal,
                                    method: 'POST',
                                    headers: {
                                        'Content-Type': 'application/json',
                                        'Accept': 'application/json'
                                    },
                                    body: JSON.stringify(adIds)
                                }).then(res => res.json())
                                    .then(res => {this.setState({order: res})})
                                    //.then(res => console.log(res))
                                    .catch(e => console.error("Critical failure: " + e.message));})
            .catch(e => console.error("Critical failure: " + e.message));
    }

    cancelOrder = () => {
        fetch('/orderroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 1,
                ids: []
            })
        }).then(() => this.setState({canceled: true}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    removeFromCart = () => {
        let cart = JSON.parse(window.localStorage.getItem(this.props.loggedInUserId));
        const adIds = this.state.adIds;
        adIds.forEach(adId => {
            cart.splice(cart.indexOf(adId.toString()), 1);
        })
        window.localStorage.setItem(this.props.loggedInUserId, JSON.stringify(cart));
    }

    finishOrder = () => {
        const price = this.priceSum(this.state.order, this.state.order.length);
        const postage = this.postageSum(this.state.order, this.state.order.length);
        fetch('/orderroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 3,
                order: {
                    ids: this.state.adIds,
                    price: price,
                    postage: postage,
                    seller_id: this.state.order[0].user_id,
                    seller: this.state.order[0].username,
                    status: 1
                }

            })
        }).then(res => res.json())
        .then(res => {console.log(res.success); if (res.success) {this.setState({finished: true}, () => console.log(this.state));} else {this.setState({finished: true, error: true}, () => console.log(this.state));}
                        this.removeFromCart();})
        .catch(e => console.error("Critical failure: " + e.message));
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    handleOrder = () => {
        this.setState({orderStatus: 'ORDERED!'});
    }

    priceSum = (order, n) => {
        let price = 0;
        for (let i = 0; i < n; i++) {
            price += order[i].price;
        }
        return price;
    }
    postageSum = (order, n) => {
        let weight = 0;
        let postage = 0;
        for (let i = 0; i < n; i++) {
            weight += order[i].weight;
        }
        postage = weight/100 +1;
        return postage;
    }

    switchRender = () => {
        if (!this.state.order.length) {
            return <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                        <br/>
                        <h1>O NE! NEKAJ JE ŠLO NAROBE. IZDELKI NISO VEČ NA VOLJO.</h1>
                        <button className = 'btnb'><NavLink exact to = '/shoppingcart' style={{color:'black', textDecoration:'none'}}>NAZAJ NA KOŠARICO</NavLink></button>
                        <br/>
                    </div>
        }
        else if (this.state.order.length && !this.state.canceled && !this.state.finished) {
            return <div style = {{padding: '30px', margin: '0 auto'}}>
                    <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                        <br/>
                        {this.state.order.map(item => <OrderListing key ={item.ad_id} description = {item}/>)}
                        <br/>
                        <div>
                            <p>Cena: {price} €</p>
                            <p>Cena poštnine: {postage} €</p>
                            <p style = {{fontWeight: 'bold'}}>Skupaj: {price + postage} €</p>
                            <button className = 'btnb' onClick = {this.finishOrder}>ZAKLJUČI NAROČILO</button>
                            <button className = 'btnb' onClick = {this.cancelOrder}>PREKLIČI NAROČILO</button>
                            <br/>
                            <p>{this.state.orderStatus}</p>
                        </div>
                        <br/>
                    </div>
                </div>
        } else if (this.state.canceled && !this.state.finished) {
            return <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                        <br/>
                        <h1>NAROČILO PREKLICANO!</h1>
                        <button className = 'btnb'><NavLink exact to = '/shoppingcart' style={{color:'black', textDecoration:'none'}}>NAZAJ NA KOŠARICO</NavLink></button>
                        <br/>
                    </div>
        } else if (this.state.finished && !this.state.canceled) {
            return <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                        <br/>
                        {!this.state.error && <h1>NAROČILO ZAKLJUČENO!</h1>}
                        {this.state.error && <h1>NAPAKA! EDEN ALI VEČ IZDELKOV ŽAL NI VEČ NA VOLJO!</h1>}
                        <button className = 'btnb'><NavLink exact to = '/' style={{color:'black', textDecoration:'none'}}>DOMOV</NavLink></button>
                        <br/>
                    </div>
        } else {
            return <div><br/><br/></div>
        }

    }

    render() {
        const order = this.state.order;
        const n = order.length;

        const price = this.priceSum(order, n);
        const postage = this.postageSum(order, n);

        if (!this.state.order.length) {
            return <div className="App">
                <UpperLine/>
                <Header/>
                <NavBar/>
                <BreadCrumbs pathname = {this.props.location.pathname}/>
                <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                    <br/>
                    <h1>O NE! NEKAJ JE ŠLO NAROBE. IZDELKI NISO VEČ NA VOLJO.</h1>
                    <button className = 'btnb'><NavLink exact to = '/shoppingcart' style={{color:'black', textDecoration:'none'}}>NAZAJ NA KOŠARICO</NavLink></button>
                    <br/>
                </div>
            <Bottom/>
        </div>   
        }
        else if (n && !this.state.canceled && !this.state.finished) {
            return <div className="App">
            <UpperLine/>
            <Header/>
            <NavBar/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
            <div style = {{padding: '30px', margin: '0 auto'}}>
                <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                    <br/>
                    {this.state.order.map(item => <OrderListing key ={item.ad_id} description = {item}/>)}
                    <br/>
                    <div>
                        <p>Cena: {price} €</p>
                        <p>Cena poštnine: {postage} €</p>
                        <p style = {{fontWeight: 'bold'}}>Skupaj: {price + postage} €</p>
                        <button className = 'btnb' onClick = {this.finishOrder}>ZAKLJUČI NAROČILO</button>
                        <button className = 'btnb' onClick = {this.cancelOrder}>PREKLIČI NAROČILO</button>
                        <br/>
                        <p>{this.state.orderStatus}</p>
                    </div>
                    <br/>
                </div>
            </div>
            <Bottom/>
        </div>   
        }
        else if (this.state.canceled && !this.state.finished) {
            return <div className="App">
                        <UpperLine/>
                        <Header/>
                        <NavBar/>
                        <BreadCrumbs pathname = {this.props.location.pathname}/>
                        <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                            <br/>
                            <h1>NAROČILO PREKLICANO!</h1>
                            <button className = 'btnb'><NavLink exact to = '/shoppingcart' style={{color:'black', textDecoration:'none'}}>NAZAJ NA KOŠARICO</NavLink></button>
                            <br/>
                        </div>
                        <Bottom/>
                    </div>
        }
        else if (this.state.finished && !this.state.canceled) {
            return <div className="App">
            <UpperLine/>
            <Header/>
            <NavBar/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
            <div style = {{backgroundColor: 'rgb(255,255,255,0.7)', margin:'0 auto'}}>
                <br/>
                {!this.state.error && <h1>NAROČILO ZAKLJUČENO!</h1>}
                {this.state.error && <h1>NAPAKA! EDEN ALI VEČ IZDELKOV ŽAL NI VEČ NA VOLJO!</h1>}
                <button className = 'btnb'><NavLink exact to = '/' style={{color:'black', textDecoration:'none'}}>DOMOV</NavLink></button>
                <br/>
            </div>
            <Bottom/>
        </div>
        }
        else { return <div className="App">
                        <UpperLine/>
                        <Header/>
                        <NavBar/>
                        <BreadCrumbs pathname = {this.props.location.pathname}/>
                        <div>
                            <br/>
                            <br/>
                        </div>
                        <Bottom/>
                    </div>
                }
    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };

  const mapDispatchToProps = {
    logIn: logIn,
    logOut: logOut
  };

export default connect(mapStateToProps, mapDispatchToProps) (OrderPage);
