import React, { Component } from 'react';
import { connect } from 'react-redux';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import NewMsg from './NewMsg';
import '../../App.css';
import { NavLink } from 'react-router-dom';
import NavBar from '../../components/NavBar';
import Bottom from '../../components/Bottom';


class Conversation extends Component {
	constructor(props) {
		super(props);
        this.state = {
            time: null,
            interlocutor: '',
            messages: [],
            newMsg: ''
        }
    }

    abortController = new AbortController();

    componentDidMount() {
        this.msgFetch();
        this.interval = setInterval(() => {this.msgFetch();}, 30000);
    }

    msgFetch = () => {
        const iLId = this.interlocutorId();
        this.interlocutor(iLId);
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 1,
                conversantId: iLId
            })
        })
        .then(res => res.json())
        .then(results => {
            const messages = results.map(result => ({time: result.date_created, sender: result.sender, msg: result.msg}));
            this.setState({messages})
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }

    componentWillUnmount() {
        this.abortController.abort();
        clearInterval(this.interval);
    }

    sendMsg = (newMsg) => {
        this.setState({newMsg})
        const receiverId = this.interlocutorId();
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 2,
                receiverId: receiverId,
                msg: newMsg
            })
        }).then(res => res.json())
        .then(res => res.success && this.setState(state => {const messages = [...state.messages, ({time: 'now', sender: this.props.loggedInUser, msg: state.newMsg})]; return {...state, messages, newMsg: ''}}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    interlocutorId() {
        function subStrAfterChars(str, char) {
            return str.substring(str.indexOf(char) + 1);
        }
        const interlocutorId = subStrAfterChars(window.location.href, '?');
        return interlocutorId;
    }

    interlocutor = (iLId) => {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 4,
                iLId: iLId,
            })
        }).then(res => res.json())
        .then(res => this.setState({interlocutor: res[0].first_name}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    render() {

        const messages = this.state.messages;
        const N = this.state.messages.length;
        const n = 5;

        return (
            <div className = 'App'>
            <UpperLine/>
            <Header/>
            <NavBar/>
                <div style = {{backgroundColor: 'rgb(255, 255, 255, 0.8)'}}>
                    <p>{this.state.time}</p>
                    <br/>
                    <p style = {{fontSize: '30px'}}>POGOVOR S/Z: <b>{this.state.interlocutor}</b></p>
                    {N > n && <button className = 'btn'><NavLink exact to = {this.props.location.pathname + '/all?' + this.interlocutorId()} style = {{color: 'black', textDecoration: 'none'}}>POKAŽI STAREJŠA SPOROČILA</NavLink></button>}
                    <br/>
                    <ul style = {{display: 'inline-block', maxWidth: '60vw', paddingLeft: '0px'}}>
                    {N > n && (
                    messages.slice(N-n, N)
                    .map((msg, index) =>
                        <li key={index} style={{listStyleType: 'none', lineHeight: '35px'}}>
                            {msg.time.substring(8,10)}-{msg.time.substring(5,7)}-{msg.time.substring(0,4)} {msg.time.substring(11,16)}: <b>{msg.sender}</b>: {msg.msg}</li>))}
                    {N <= n && this.state.messages.map((msg, index) =>
                        <li key={index} style={{listStyleType: 'none', lineHeight: '35px'}}>
                            {msg.time.substring(8,10)}-{msg.time.substring(6,7)}-{msg.time.substring(0,4)} {msg.time.substring(11,16)}: <b>{msg.sender}</b>: {msg.msg}</li>)}
                    </ul>
                    <br/>
                    <br/>

                    <div>
                        <NewMsg callBackParent = {this.sendMsg}/>
                    </div>
                </div>
                <Bottom/>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
};

export default connect(mapStateToProps) (Conversation)
