import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import NewMsg from './NewMsg';
import '../../App.css';
import { Redirect } from 'react-router-dom';


class AllMessages extends Component {
	constructor(props) {
		super(props);
        this.state = {
            interlocutor: '',
            messages: []
        }
    }

    abortController = new AbortController();

    componentDidMount() {
        this.msgFetch();
    }

    msgFetch = () => {
        const iLId = this.interlocutorId();
        this.interlocutor(iLId);
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 1,
                conversantId: iLId
            })
        })
        .then(res => res.json())
        .then(results => {
            const messages = results.map(result => ({time: result.date_created, sender: result.sender, msg: result.msg}));
            this.setState({messages})
        })
        .catch(e => console.error("Critical failure: " + e.message));
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    interlocutorId() {
        function subStrAfterChars(str, char) {
            return str.substring(str.indexOf(char) + 1);
        }
        const interlocutorId = subStrAfterChars(window.location.href, '?');
        return interlocutorId;
    }

    interlocutor = (iLId) => {
        fetch('/messageroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 4,
                iLId: iLId,
            })
        }).then(res => res.json())
        .then(res => this.setState({interlocutor: res[0].first_name}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    render() {

        const messages = this.state.messages;

        return (
            <div className = 'App'>
            <UpperLine/>
            <Header/>
            <BreadCrumbs pathname = {this.props.location.pathname}/>
                <div style = {{backgroundColor: 'rgb(255, 255, 255, 0.8)'}}>
                    <br/>
                    <p style = {{fontSize: '30px'}}>ZGODOVINA POGOVOROV S/Z: <b>{this.state.interlocutor} </b>
                    <button className = 'btn'><NavLink exact to = {'/chatpage/conversation?' + this.interlocutorId()} style = {{color: 'black', textDecoration: 'none'}}>NAZAJ</NavLink></button></p>
                    <br/>
                    <ul style = {{display: 'inline-block', maxWidth: '60vw'}}>
                    {messages.map((msg, index) =>
                        <li key={index} style={{listStyleType: 'none', lineHeight: '35px'}}>
                            {msg.time.substring(8,10)}-{msg.time.substring(6,7)}-{msg.time.substring(0,4)} {msg.time.substring(11,16)}: <b>{msg.sender}</b>: {msg.msg}</li>)}
                    </ul>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
};

export default connect(mapStateToProps) (AllMessages)
