import React from 'react';
import {NavLink} from 'react-router-dom';
import '../../App.css';
import * as Constants from '../../constants';


const CartListingSeller = (props) => {

    const findSubcategory = (category, index) => {
        let sc;
        switch(category) {
            case 'women':
                sc = Constants.women[index];
                break;
            case 'men':
                sc = Constants.men[index];
                break;
            case 'kids':
                sc = Constants.kids[index];
                break;
            default:
                sc = null;
        }
        return sc.label;
    }

    const des = props.description;

    const removeFromCart = () => {
        let cart = JSON.parse(window.localStorage.getItem(props.loggedInUserId));
        if (cart !== null) {
            cart.splice(cart.indexOf(des.ad_id.toString()), 1);
            window.localStorage.setItem(props.loggedInUserId, JSON.stringify(cart));
        }
        cart = JSON.parse(window.localStorage.getItem(props.loggedInUserId));
        props.callBackParent();
    }

    if(des) {
        let subcategory = findSubcategory(Constants.category[des.category].value, des.subcategory);
        return (
            <div style = {{backgroundColor: 'rgb(255,255,255,0.6)', padding: '30px', margin: '0 auto'}}>
                <NavLink exact to = {'/item?' + des.ad_id}>
                    <img src={des.photo1_url}
                        alt = {des.photo1_url + '_photo'}
                        height = '300'
                        style = {{border: '2px solid white'}}/>   
                </NavLink>
                <div className="description">
                    <p style = {{fontWeight: 'bold'}}>{subcategory}</p>
                    <p>Velikost: {Constants.clothsize[des.size].label}</p>
                    <p>ID oglasa: {des.ad_id}</p>
                    <p>Cena: {des.price} â‚¬</p>
                    <p>Prodajalec: {des.username}</p>
                    <div style={{textAlign: 'right'}}><button className = 'btnx' onClick = {removeFromCart}>X ODSTRANI</button></div>

                </div>
            </div>
        )
        } else return <div><h2>{this.props.description.price} â‚¬</h2></div>

}

export default CartListingSeller;
