import React from 'react';
import Images from './images';
import Pagers from './pagers';
import Popup from 'reactjs-popup';
import { FaRegWindowClose } from 'react-icons/fa';
import '../../App.css';

  class Slider extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            active: 0,
            origUrls: [],
            focused: -1
        };
        this.onPagerClicked = this.onPagerClicked.bind(this);
    }

    componentDidMount() {
        this.setState({origUrls: this.props.origPhotos});
    }

    onPagerClicked(newState) {
      this.setState({ active: newState });
    }

    onImageClicked = (index) => {
        this.setState({focused: index});
    }

    closeModal = () => {
        this.setState({focused: -1});
    }

    render() {

        const iconStyle = {
            margin: '-50px, -50px',
            verticalAlign: 'top',
            border: 'none',
            backgroundColor: 'rgb(255, 255, 255, 0)',
            verticalAlign: 'middle'
        }
        //var self = this;
        if(this.state.focused !== -1) {
            return (
                <Popup open = {true}
                    position = 'center center'
                    onClose={this.closeModal}>
                    <div style = {{display: 'flex', width: '100%'}}>
                        <img src = {'/' + this.state.origUrls[this.state.focused]}
                            alt = {this.state.origUrls[this.state.focused] + '_photo'}
                            style = {{width: '100%', margin: '0 auto'}}/>
                        <button type = 'button' style = {iconStyle} onClick = {this.closeModal}>
                            <FaRegWindowClose size = '2em'/>
                        </button>
                    </div>
                </Popup>
            )
        } else
        return(
            <div className = 'imgSlider'>
                <Images items = {this.props.slides}
                        useImages = {this.props.useImages}
                        active = {this.state.active}
                        adId = {this.props.adId}
                        photos = {this.props.photos}
                        callbackParent={this.onImageClicked}>
                </Images>
                <Pagers items = {this.props.slides}
                        callbackParent={this.onPagerClicked}>
                </Pagers>
            </div>
        )
    }

  }

  export default Slider;
