import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import '../../App.css';


class Profile extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            orders: []
        };
    }

    abortController = new AbortController();
	componentDidMount() {
        fetch('/orderroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 5
            })
        })
        .then(res => res.json())
        .then(orders => this.setState({orders}))
        .catch(e => console.error("Critical failure: " + e.message));   
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    render() {

        if(!this.state.orders.length) {
            return (<div><h2 style = {{color: 'white'}}>NI PRODANIH IZDELKOV.</h2></div>)
        } else {
            return (
                <div className = 'profile'>
                    <br/>
                    <br/>
                    <p><b>IME:</b> BEBE</p>
                    <p><b>PRIIMEK:</b> REXHA</p>
                    <p><b>NASLOV ZA POÅ ILJANJE:</b> STARA CESTA 11, 2000 MARIBOR, SLOVENIJA
                    <button style = {{backgroundColor: 'rgb(255, 255, 255, 0)', fontWeight: 'bold', border: 'none'}}>(UREDI)</button></p>
                    <p><b>TELEFON:</b> 070 005 005</p><button>(UREDI)</button>
                </div>
            )
        }

    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };


export default connect(mapStateToProps) (Profile);
