import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { logIn, logOut } from '../../redux/actions';
import '../../App.css';


class Sold extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            orders: []
        };
    }

    abortController = new AbortController();
	componentDidMount() {
        fetch('/orderroutes', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                type: 5
            })
        })
        .then(res => res.json())
        .then(orders => this.setState({orders}))
        .catch(e => console.error("Critical failure: " + e.message));   
    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    render() {

        if(!this.state.orders.length) {
            return (<div><h2 style = {{color: 'white'}}>NI PRODANIH IZDELKOV</h2></div>)
        } else {
            return (
                <div className = 'orderListWrapper'>
                    <br/>
                    <br/>
                    {this.state.orders.map((order) =>
                        <li key={order.order_id} style={{ listStyleType: "none" }}>
                            Številka naročila: <span style={{fontWeight: 'bold'}}> {order.order_id} </span>
                            Datum naročila:  <span style={{fontWeight: 'bold'}}>{order.date_modified.substr(0,10)} </span>
                            Cena:  <span style={{fontWeight: 'bold'}}>{order.price} € </span>
                            Poštnina:  <span style={{fontWeight: 'bold'}}>{order.postage} € </span>
                            Kupec: <span style={{fontWeight: 'bold'}}>{order.buyer} </span>
                            Izdelki: {order.ad_ids.split(',').map(id => <button key = {id} className='btn_nopad'>
                                                                            <NavLink to={"/item?" + id} style = {{color:'black', textDecoration:'none'}}>{id}</NavLink>
                                                                        </button>)}
                        </li>
                    )}
                </div>
            )
        }

    }

}

const mapStateToProps = state => {
    return {
      loggedInUser: state.loggedInUser,
      loggedInUserId: state.loggedInUserId
    };
  };


export default connect(mapStateToProps) (Sold);
