import React from 'react';
import Header from '../components/Header';
import UpperLine from '../components/UpperLine';
import Bottom from '../components/Bottom';
import '../App.css';

class ForgotPassword extends React.Component {

    constructor(props) {
		super(props);
		this.state = {
            email: '',
            success: ''
        };
        this.handleChange = this.handleChange.bind(this);
    }

    abortController = new AbortController();

    sendRequestForReset = (e) => {
        e.preventDefault();

        fetch('/forgotpassword', {
            signal: this.abortController.signal,
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                email: this.state.email
            })
        }).then(res => res.json())
            .then(res => {console.log(res.success.toString()); this.setState({success: res.success})});

    }

    componentWillUnmount() {
        this.abortController.abort();
    }

    handleChange(event) {
		this.setState({
			[event.target.name]: event.target.value
		})
    }

    //<button type = 'submit' style = {{paddingLeft: '200px'}}>RESET PASSWORD</button>

    render() {

        return(
            <div className = 'app'>
                <UpperLine/>
                <Header/>
                <br/><br/>
                <form onSubmit={this.sendRequestForReset} style = {{textAlign: 'center', marginTop: '100px', marginBottom: '300px'}}>
                    <input  type = 'email'
                            name = 'email'
                            value = {this.state.email}
                            placeholder = 'Vpiši svoj e-naslov ...'
                            style = {{width: '300px', marginRight: '10px'}}
                            onChange = {this.handleChange}/>
                    <button type = 'submit' className = 'btn'>POŠLJI ZAHTEVO ZA SPREMEMBO GESLA</button>
                </form>
                <p style = {{backgroundColor: 'white', textAlign: 'center', lineHeight: '50px'}}>{this.state.success}</p>
                <Bottom/>
            </div>
        )
    }

}

export default ForgotPassword;
