import React, {Component} from 'react';
import { FaEyeSlash, FaEye } from 'react-icons/fa';
import NumberFormat from 'react-number-format';

class SignUpForm extends Component {

    constructor(props) {
		super(props);
		this.state = {
            visible: false
		};
    }

    visibilityChange = () => {
        this.setState({visible: !this.state.visible})
    }

    render() {

        const style={
            textAlign: "center",
            margin: "30px",
            paddingLeft: "30vw",
            paddingRight: "30vw"
        }

        const iconStyle = {
            margin: 'auto -50px',
            paddingTop: '8px',
            border: 'none',
            backgroundColor: 'rgb(255, 255, 255, 0)',
            verticalAlign: 'middle'
        }

        return(
            <div style={style}>
                <form onSubmit={this.props.handleClickSignUp}>
                    <input
                        type="text"
                        name="firstName"
                        value={this.props.firstName}
                        placeholder="Ime"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="lastName"
                        value={this.props.lastName}
                        placeholder="Priimek"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="street"
                        value={this.props.street}
                        placeholder="Ulica in hišna številka"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="number"
                        name="postNumber"
                        value={this.props.postNumber}
                        placeholder="Poštna številka"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <input
                        type="text"
                        name="town"
                        value={this.props.town}
                        placeholder="Kraj"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <NumberFormat
                        value={this.props.phoneNumber}
                        placeholder='Številka mobilnega telefona'
                        format='+386 (0)## ### ###'
                        //allowEmptyFormatting mask="_"
                        allowNegative={false}
                        isNumericString={true}
                        onValueChange={this.props.changePhoneNumber}/>
                    <br/>
                    <input
                        type="email"
                        name="email"
                        value={this.props.email}
                        placeholder="E-naslov"
                        onChange={this.props.handleChange}>
                    </input>
                    <br/>
                    <div style = {{display: 'inline-flex', width: '100%'}}>
                    <input
                        type={this.state.visible ? 'text' : 'password'}
                        name="password"
                        value={this.props.password}
                        placeholder="Geslo"
                        onChange={this.props.handleChange}>
                    </input>
                    {this.state.visible ? <button type = 'button' style = {iconStyle} onClick = {this.visibilityChange}>
                                                <FaEyeSlash size = '2em'/>
                                            </button> :
                                            <button type = 'button' style = {iconStyle} onClick = {this.visibilityChange}>
                                                <FaEye size = '2em'/>
                                            </button>}
                    </div>
                    <br/>
                    <button type = 'submit' className = 'btnb' id = 'signup'>ODDAJ</button>
                </form>
            </div>
        );
    }

}

export default SignUpForm;
