import React from 'react';
import { Redirect } from 'react-router-dom';
import UpperLine from '../../components/UpperLine';
import Header from '../../components/Header';
import NavBar from '../../components/NavBar';
import UserSideBar from './UserSideBar';
import UserListings from './UserListings';
import PastOrders from './PastOrders';
import Bottom from '../../components/Bottom';
import WatchList from './WatchList';
import Sold from './Sold';
import BreadCrumbs from '../../components/BreadCrumbs';
import '../../App.css';


class UserPage extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
            userId: '0',
            active: 'oglasi'
        };

    }
    abortController = new AbortController();

    componentWillUnmount() {
        this.abortController.abort();
    }

    componentDidMount() {
        fetch('/sessionroutes', {signal: this.abortController.signal})
        .then(res => res.json())
        .then(res => this.setState({userId: res.userId}))
        .catch(e => console.error("Critical failure: " + e.message));
    }

    callBackSideBar = (sideBarElement) => {
        this.setState({active: sideBarElement});
    }

    switchFunction = () => {
        switch (this.state.active) {
            case 'oglasi':
                return <UserListings/>;
            case 'narocila':
                return <PastOrders/>;
            case 'shranjeno':
                return <WatchList/>;
            case 'prodano':
                return <Sold/>;
            default:
                return null;
        }
    }

    render() {

        console.log(this.state.userId);
        if(this.state.userId === '0') {
            return (
            <div className="App">
                <UpperLine/>
                <Header/>
                <NavBar/>
                <BreadCrumbs pathname = {this.props.location.pathname}/>
                <div className="userPage">
                    <UserSideBar callBack={this.callBackSideBar}
                                ads={this.state.active==='oglasi' ? 'activeStyle' : 'nonActive'}
                                orders={this.state.active==='narocila' ? 'activeStyle' : 'nonActive'}
                                saved={this.state.active==='shranjeno'? 'activeStyle' : 'nonActive'}
                                sold={this.state.active==='prodano' ? 'activeStyle' : 'nonActive'}/>
                    <div style={{width: "80vw"}}>
                        {this.switchFunction()}
                    </div>
                </div>
                <Bottom/>
            </div>
            );
        } else { return (<Redirect to = '/404'></Redirect>)

        }

    }

}

export default UserPage;
